task main()
{
SensorType[S1] = sensorTouch;// intiate the sensor touch
SensorType[S2] = sensorCOLORFULL;//intiate color sensor and turn on all the lights

// declare all variable that are going to be used
int color[25]; // will store the number corresponding to the colors
float  timer[25];// will store the time corresponding to each color
int notesNumber = 0; // counter for index of array

/*
all the colors follow the same algorithim
The colors in order of appearence
1 = BLACKCOLOR or NOT IN RANGE
5 = REDCOLOR
4 = YELLOWCOLOR
3 = GREENCOLOR
2 = BLUECOLOR
6 = WHITECOLOR

RANGE OF SENSOR IS 1-2 CM
*/
motor[motorA] = motor[motorB] = -15;
while(SensorValue[S2] != 1) // while the color is not black. Main loop
{

  time10[0]=0; // initiate the timer
  
  if(SensorSensorValue[S2] == 6)
    { 
    if(SensorValue[S2] == 1)
   break;
    }


 else if (SensorValue[S2] == 5) // if the color sensed in red
 {
  color[notesNumber]  = 5 ;// store 5 in the array at index counter

 while(SensorValue[S2] == 5)//and while its sensing red, timer is set
  timer[notesNumber] = time10[0];

 while (SensorValue[S2] ==5);//once it stops seeing red, counter ++
	notesNumber++;
 }

else if (SensorValue[S2] == 4)
 {
  color[notesNumber]  = 4;
 while(SensorValue[S2] == 4)
  timer[notesNumber] = time10[0];

 while (SensorValue[S2] ==4);
	notesNumber++;
 }

else if (SensorValue[S2] == 3)
 {
  color[notesNumber]  = 3 ;
 while(SensorValue[S2] == 3)
  timer[notesNumber] = time10[0];

 while (SensorValue[S2] ==3);
	notesNumber++;
 }

else if (SensorValue[S2] == 2)
 {
  color[notesNumber]  = 2 ;
 while(SensorValue[S2] == 2)
  timer[notesNumber] = time10[0];

 while (SensorValue[S2] ==2);
	notesNumber++;
 }


}

///*

// OUTPUT FOR TESTING PURPOSES

motor[motorA] = motor[motorB] = 0;

nxtDisplayString(2, "WE ARE DONE :) !");


while(SensorValue[S1] == 0) ;
while(SensorValue[S1] == 1) ;


nxtDisplayString(2, " =%d" , color[notesNumber]);
nxtDisplayString(3, " =%d", notesNumber);
//nxtDisplayString(4, " =%d", counterTime[0]);
nxtDisplayString(5, " =%f" , timer[0]);



wait10Msec(1000);

//*/
}
